div.script:
//
    function initMap() {
        const locations = [
            { lat: 42.5986, lng: -5.5671, name: 'José Luis Sevillano', area: 'León' },
            { lat: 42.8805, lng: -8.5456, name: 'José Mato Miguelez', area: 'Galicia (Santiago de Compostela)' },
            { lat: 40.2078, lng: -5.0894, name: 'Gabriel Méndez Nombela', area: 'Sierra de Gredos (Arenas de San Pedro)' },
            { lat: 40.3456, lng: -1.1065, name: 'José Miguel Delgado Almazán', area: 'Teruel' }
        ];
        const mapCenter = { lat: 40.416775, lng: -3.703790 };
        const map = new google.maps.Map(document.getElementById("map"), {
            zoom: 6,
            center: mapCenter,
            mapId: 'ANFITRIONES_MAP_ID'
        });
        const infowindow = new google.maps.InfoWindow();
        locations.forEach(location => {
            const marker = new google.maps.Marker({
                position: { lat: location.lat, lng: location.lng },
                map: map,
                title: `${location.name} (${location.area})`,
                animation: google.maps.Animation.DROP,
            });
            marker.addListener('click', () => {
                const content = `<div style="font-weight: bold;">${location.name}</div><div>${location.area}</div>`;
                infowindow.setContent(content);
                infowindow.open(map, marker);
            });
        });
    }
//
